// resourceUsage displays the usage of the resource command and its subcommands.
func resourceUsage() {
	fmt.Fprintln(os.Stderr, `The resource service provides details about all kind of resources`)
	fmt.Fprintf(os.Stderr, "Usage:\n    %s [globalflags] resource COMMAND [flags]\n\n", os.Args[0])
	fmt.Fprintln(os.Stderr, "COMMAND:")
	fmt.Fprintln(os.Stderr, `    query: Find resources by a combination of name, kind, catalog, categories, platforms and tags`)
	fmt.Fprintln(os.Stderr, `    list: List all resources sorted by rating and name`)
	fmt.Fprintln(os.Stderr, `    versions-by-id: Find all versions of a resource by its id`)
	fmt.Fprintln(os.Stderr, `    by-catalog-kind-name-version: Find resource using name of catalog & name, kind and version of resource`)
	fmt.Fprintln(os.Stderr, `    by-version-id: Find a resource using its version's id`)
	fmt.Fprintln(os.Stderr, `    by-catalog-kind-name: Find resources using name of catalog, resource name and kind of resource`)
	fmt.Fprintln(os.Stderr, `    by-id: Find a resource using it's id`)
	fmt.Fprintln(os.Stderr)
	fmt.Fprintln(os.Stderr, "Additional help:")
	fmt.Fprintf(os.Stderr, "    %s resource COMMAND --help\n", os.Args[0])
}
